package adventofcode2015

import scala.annotation.tailrec

object Day1 extends App {
  def parseParens(input: String): Int = {
    var count = 0

    input.foreach {
      paren => paren.toString match {
        case "(" => count += 1
        case ")" => count -= 1
      }
    }
    count
  }

  def parseParensImmutable(input: String): Int = {
    input.foldLeft(0)(
      (count, paren) => paren.toString match {
        case "(" => count + 1
        case ")" => count - 1
      }
    )
  }

  def getBasementPosition(input: String): Int = {
    var count = 0
    var basementIdx = 0

    for (i <- 0 to input.length) {
      if (count < 0) {
        basementIdx = i
        return basementIdx
      }
      count = if (input(i).toString == "(") count + 1 else count - 1
    }
    basementIdx
  }

  def getBasementPositionTailRec(input: String): Int = {
    @tailrec
    def loop(count: Int, index: Int): Int = {
      if (count < 0) return index
      // return -1 if santa never reaches basement
      if (index == input.length) return -1
      if (input(index) == '(') {
        loop(count + 1, index + 1)
      } else {
        loop(count - 1, index + 1)
      }
    }

    loop(count = 0, index = 0)
  }

